function bytesToUint16(dataArray, isLE, offset){
    if (dataArray.length < 2 * (offset + 1)) {
        return "Invalid [dataArray] size!";
    }

    const buf = Buffer.from(dataArray);
    return isLE ? buf.readUInt16LE(offset) : buf.readUInt16BE(offset);
}

function bytesToInt16(dataArray, isLE, offset){
    if (dataArray.length < 2 * (offset + 1)) {
        return "Invalid [dataArray] size!";
    }

    const buf = Buffer.from(dataArray);
    return isLE ? buf.readInt16LE(offset) : buf.readInt16BE(offset);
}

function bytesToUint32(dataArray, isLE, offset){
    if (dataArray.length < 4 * (offset + 1)) {
        return "Invalid [dataArray] size!";
    }

    const buf = Buffer.from(dataArray);
    return isLE ? buf.readUInt32LE(offset).toString(16) : buf.readUInt32BE(offset).toString(16);
}

function bytesToInt32(dataArray, isLE, offset){
    if (dataArray.length < 4 * (offset + 1)) {
        return "Invalid [dataArray] size!";
    }

    const buf = Buffer.from(dataArray);
    return isLE ? buf.readInt32LE(offset).toString(16) : buf.readInt32BE(offset).toString(16);
}

function bytesToFloat(dataArray, isLE, offset){
    if (dataArray.length < 4 * (offset + 1)) {
        return "Invalid [dataArray] size!";
    }

    const buf = Buffer.from(dataArray);
    return isLE ? buf.readFloatLE(offset) : buf.readFloatBE(offset);
}

function bytesToDouble(dataArray, isLE, offset) {
    if (dataArray.length < 8  * (offset + 1)) {
        return "Invalid [dataArray] size!";
    }

    const buf = Buffer.from(dataArray);
    return isLE ? buf.readDoubleLE(offset) : buf.readDoubleBE(offset);
}

